    function codificaPDF($file){
      $base64="error al codificar";
      if(file_exists ($file)){  
        try{
          $base64=base64_encode(file_get_contents($file));
        }catch(Exception $e){
          $base64 = $e;
        }  
      }
      else{
        $base64 = "archivo $file no existe";
      }
      
      return $base64;
    }

    function decodificaPDF($base64, $file){
      $pdf_decoded = base64_decode ($base64);
      //Write data back to pdf file
      //$pdf = fopen ('test.pdf','w');
      $pdf = fopen ($file,'w');
      fwrite ($pdf,$pdf_decoded);
      //close output file
      fclose ($pdf);
      //echo 'Done';
      return true;
    }
